$(document).ready(function(){$(".page").each(function(e){$(this).click(function(a){var p=a.pageX,c=a.pageY,t=e+1;t>=$(".page").length&&(t=0),$(".page:eq("+t+")").css("z-index",parseInt($(this).css("z-index"))+1),$(".page:eq("+t+")").css("clip-path","circle(0% at "+p+"px "+c+"px)"),anime({targets:$(".page")[t],update:function(e){$(".page:eq("+t+")").css("clip-path","circle("+2*e.progress+"% at "+p+"px "+c+"px)")}})})})});